import React from "react";
import "./SearchBar.css";


export const SearchBar = ({ userInput,onFormChange, onFormSubmit,names}) =>{

  const handleChange = (event) => {
    onFormChange(event.target.value)
  }

  const handleSubmit = (event) =>{
    event.preventDefault()
    onFormSubmit()
  }
  return (
    <>
      <form onSubmit={handleSubmit}>
        <input class="search" type="text" size={"30"} value = {userInput} onChange = {handleChange}></input>
        <input class="button" type='submit' value = "search"></input>
      </form>
      <div className="dataResult">
        {names.map((value,key)=>{
          return <div class="dataItem"> {value.title} </div>
        })}
      </div>
    </>
  );
}

export default SearchBar;
