import React from "react";
import "./SearchBar.css";


export const SearchBar = ({ userInput,onFormChange, onFormSubmit}) =>{

  const handleChange = (event) => {
    onFormChange(event.target.value)
  }

  const handleSubmit = (event) =>{
    event.preventDefault()
    onFormSubmit()
  }
  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
        <label id="fname">Enter a movie you like: </label>
        </div>
        <input class="search" type="text" value = {userInput} onChange = {handleChange}></input>
        <input class="button" type='submit' value = "search"></input>
      </form>
    </>
  );
}

export default SearchBar;
